						if(allLoaded)				{					$(window).unbind('scroll');					return;				}								if(! loading){					loading=true;					$('#more').html('загружается...');					$.ajax({url: 'ajax_serials/', data: {p: page++}, complete: function(res){						$('#serials').append(res.response);						loading=false;						$('#more').html('загрузить еще');						if(! res.response)						{							$('#more').hide('');							$(window).unbind('scroll');						}					}});								}							}		});	});</script><?	if($url['page']=='my_serials')	{		?>					<div style='font-weight:bold;'>				Мои сериалы			</div>				<?	}?>$=block_serials<script>	document.write('<style>.pages{display:none}</style>');</script><div id=serials>	$=block_pages</div><div id=more style='border-top:1px solid #999;padding:3px 0 12px 0;clear:both;color:gray'>	загрузить еще</div><div style='clear:left'></div>